import React, { useState } from 'react';
import "./GridGame.css";
import {
  DndContext, 
  useDraggable, 
  DragEndEvent, 
  CollisionDetection as CollisionDetectionType, 
  Modifiers, 
  UniqueIdentifier,
  DragOverlay,
} from '@dnd-kit/core';
import {Draggable} from './Draggable';
import {Droppable} from './Droppable';
import {Draggable2} from './Draggable2';

interface Props {
  modifiers?: Modifiers;
  grid?: string[][];
}

var currentTile = null;

function buildGrid() {
  var grid: string[][] = [];
  var element = 0;
  for (let y = 0; y < 10; y++) {
    grid[y] = [];
    for (let x = 0; x < 10; x++) {
      grid[y][x] = String(element);
      element++;
    }
  }

  return grid;
}


interface DraggableProps {
  handle?: boolean;
}

function DraggableItem({handle}: DraggableProps) {
  const {isDragging, setNodeRef, listeners} = useDraggable({
    id: 'draggable-item',
  });

   return (
    <Draggable
      dragging={isDragging}
      ref={setNodeRef}
      handle={handle}
      listeners={listeners}
      style={{
        opacity: isDragging ? 0 : undefined,
      }}
    />
  );
}


function DraggableItem2({handle}: DraggableProps) {
  const {isDragging, setNodeRef, listeners} = useDraggable({
    id: 'draggable-item',
  });

   return (
    <Draggable
      dragging={isDragging}
      ref={setNodeRef}
      handle={handle}
      listeners={listeners}
      style={{
        opacity: isDragging ? 0 : undefined,
      }}
    />
  );
}

export function GridGame({modifiers,}: Props) {
    var num = "-1";
    const grid = buildGrid();
    const [parent, setParent] = useState<UniqueIdentifier | null>(null);
    const [parent2, setParent2] = useState<UniqueIdentifier | null>(null);
    const [isDragging, setIsDragging] = useState(false);
    const [isDragging2, setIsDragging2] = useState(false);
    const draggable = <DraggableItem />;
    const draggable2 = <DraggableItem />;


    return (

      <DndContext
        modifiers={
          parent2 === null ? undefined: modifiers
        }
        onDragStart={() => setIsDragging2(true)}
        onDragEnd={({over}) => {
          setParent2(over ? over.id : null);
          setIsDragging2(false);
          currentTile = parent;
        }}
        onDragCancel={() => setIsDragging2(false)}
      >
          <div className = "drop">
              {parent2 === null ? draggable2 : null}
          </div>
        <DndContext 
        modifiers={
          parent === null ? undefined: modifiers
        }
        onDragStart={() => setIsDragging(true)}
        onDragEnd={({over}) => {
          setParent(over ? over.id : null);
          setIsDragging(false);
          currentTile = parent;
        }}
        onDragCancel={() => setIsDragging(false)}
        > 
          <div className = "drop">
              {parent === null ? draggable : null}
          </div>

          <div className = "grid">
            {grid.map((row: any[]) => { 
                return (
                  <div>
                    {row.map((tile:any) => {
                      return (    

                          <Droppable id={tile}>
                            {parent === tile ? draggable : ''}
                            {parent2 === tile ? draggable2 : ''}
                          </Droppable> 

                      ); 
                     })}
                  </div>
                );
            })}

             {/*draggable*/}
            {/*<DragMain/>*/}

            </div>
            <DragOverlay>
                {isDragging ? <Draggable dragging dragOverlay /> : null}
            </DragOverlay>
        </DndContext> 
            <DragOverlay>
                {isDragging2 ? <Draggable2 dragging dragOverlay /> : null}
            </DragOverlay>
        </DndContext>

    );
}


export default GridGame;
