import React, { PropsWithoutRef, useState } from 'react';
import "./GridGame.css";
import {
  DndContext, 
  useDraggable, 
  DragEndEvent, 
  CollisionDetection as CollisionDetectionType, 
  Modifiers, 
  UniqueIdentifier,
  DragOverlay,
} from '@dnd-kit/core';
import {Draggable} from './Draggable';
import {Droppable_Green} from './Droppable_green';
import {Droppable_white} from './Droppable_white';
import {Draggable2} from './Draggable2';
import {} from './DraggableProperties';
interface Props {
  modifiers?: Modifiers;
  grid?: string[][];
}

var num_G = 1;
var num_B = 2;
var draggableCheckers = new Map();
var count = 0;
/*
function Droppable(props) {
  const {isOver, setNodeRef} = useDroppable({
    id: props.id,
  });
  const style = {
    opacity: isOver ? 0.5 : 1,
  };

  return (
    <div className = "drop" ref={setNodeRef} style={style}>
      {props.children}
    </div>
  );
}
*/
/*
function DragMain() {
  const [parent, setParent] = useState(false);
  const [isDragging, setIsDragging] = useState(false);
  const draggable = (
    <Draggable id="draggable">
      drag
    </Draggable>
  );

  return (
    //<div className = "back">
      <DndContext onDragEnd={handleDragEnd}>
        {!parent ? draggable : false}
          <Droppable key = {"droppable"} id = {"droppable"} dragging = {isDragging}>
            {parent ? draggable : ''}
          </Droppable>
      </DndContext>
    //</div>
  );

  function handleDragEnd(event: DragEndEvent) {
    setParent(event.over ? !event.over.id : false);
    //if (event.over && event.over.id === 'droppable') {
      //setParent(true);
    //}
  }

}
*/


function buildGrid() {
  var grid: string[][] = [];
  var element = 1;
  for (let y = 1; y < 11; y++) {
    grid[y] = [];
    for (let x = 1; x < 11; x++) {
      grid[y][x] = String(element);
      element++;
    }
  }

  return grid;
}


interface DraggableProps {
  handle?: boolean;
}


  const [parent, setParent] = useState<UniqueIdentifier | null>(null);
  const [isDragging, setIsDragging] = useState(false);


function DraggableItem({handle}: DraggableProps) {
  const {isDragging, setNodeRef, listeners} = useDraggable({
    id: 'draggable-item' + num_G,
  });

   return (
    <Draggable
      identity={num_G}
      dragging={isDragging}
      ref={setNodeRef}
      handle={handle}
      listeners={listeners}
      style={{
        opacity: isDragging ? 0 : undefined,
      }}

    />
  );
}


function DraggableItem2({handle}: DraggableProps) {
  const {isDragging, setNodeRef, listeners} = useDraggable({
    id: 'draggable-item' + num_B,
  });
   return (
    <Draggable2
    identity={num_B}
      dragging={isDragging}
      ref={setNodeRef}
      handle={handle}
      listeners={listeners}
      style={{
        opacity: isDragging ? 0 : undefined,
      }}

    />
  );
}

export function GridGame({modifiers,}: Props) {
    const grid = buildGrid();
    const [parent, setParent] = useState<UniqueIdentifier | null>(null);
    const [parent2, setParent2] = useState<UniqueIdentifier | null>(null);
    var [parent3, setParent3] = useState<UniqueIdentifier | null>(null);
    const [isDragging, setIsDragging] = useState(false);
    const [isDragging2, setIsDragging2] = useState(false);
    const draggable2 = <DraggableItem2 />;
    num_B++;
    //var test2 = [{const [parent3, setParent3] = useState<UniqueIdentifier | null>(null)}];
   //const grid = Array(10).fill(Array(10).fill(<div className = "square_green"/>))

    //this.populateGrid();
    //this.createGrid(); 
   //const grid = Array(10).fill(Array(10).fill("A"))
    //const grid = Array(10).fill("A")
    return (
      <div>
      <DndContext 
        modifiers={
          parent === null ? undefined: parent2 === null ? undefined: modifiers
        }
        onDragStart={(event) => {
          var t = draggableCheckers.get(event.active.id);
          t[2][1](true);

          /*
          if (event.active.id == "draggable-item"+num_G) {
            setIsDragging(true); 
          } else {
            setIsDragging2(true);
          } 
          */
        }}
        onDragEnd={({over}) => {
          draggableCheckers.forEach((key : String) => {
            if (draggableCheckers.get(key)[2][1] == true) {
              draggableCheckers.get(key)[1][0](over ? over.id : null)
              draggableCheckers.get(key)[2][1](false)
            }
          });
          /*
          if (isDragging==true) {   
            setParent(over ? over.id : null);
            setIsDragging(false);
          } else {
            setParent2(over && isDragging2==true ? over.id : null);
            setIsDragging2(false);
          }
          */
        }}
        onDragCancel={() => {setIsDragging(false); setIsDragging2(false);}}
        > 
          <div className = "drop">
              {/*parent === null ? draggable : null*/}
          </div>
          <div className = "drop">  
              {parent2 === null ? draggable2 : null}
          </div>
          <div className = "grid">

            {grid.map((row: any[]) => { 
                return (
                  <div>
                    {row.map((tile:any) => {
                      var rowIndex = grid.indexOf(row);
                      if ((Number(tile)%2 == 0 && rowIndex%2 ==0) || (Number(tile)%2 != 0 && rowIndex%2 !=0)) {
                        const draggable = <DraggableItem/>;
                        //const test = draggable.type.parent;
                        draggableCheckers.set('draggable-item' + num_G, [draggable, setParent, [isDragging, setIsDragging]]);
                        <DragOverlay>
                          {isDragging ? <Draggable dragging dragOverlay /> : null}
                        </DragOverlay>
                        num_G++;
                        return (    
                          <div>
                            <Droppable_white id={tile}>
                              {parent === null && tile < 50? draggable : null}
                              {parent === tile ? draggable : ''}
                              {parent2 === tile ? draggable2 : ''}
                            </Droppable_white> 
                          </div> 
                        ); 
                      } else {
                        return (    
                          <div>
                             <Droppable_Green id={tile}>

                            </Droppable_Green> 
                          </div> 
                        ); 
                      }
                     })}

                  </div>
                );
            })}

             {/*draggable*/}
            {/*<DragMain/>*/}
            </div>


        </DndContext> 
        </div>

    );
}


export default GridGame;
