import React, { PropsWithoutRef, useState } from 'react';
import "./GridGame.css";
import {
  DndContext, 
  useDraggable, 
  DragEndEvent, 
  CollisionDetection as CollisionDetectionType, 
  Modifiers, 
  UniqueIdentifier,
  DragOverlay,
  DragStartEvent,
  DragCancelEvent,
} from '@dnd-kit/core';
import {Draggable} from './Draggable';
import {Droppable_Green} from './Droppable_green';
import {Droppable_white} from './Droppable_white';
import {Draggable2} from './Draggable2';
import {} from './DraggableProperties';
import { timeline } from 'console';
interface Props {
  modifiers?: Modifiers;
  grid?: string[][];
}

var isTurnRed = true;
var isTurnBlack = false;

function buildGrid() {
  var grid: string[][] = [];
  var element = 1;
  for (let y = 1; y < 9; y++) {
    grid[y] = [];
    for (let x = 1; x < 9; x++) {
      grid[y][x] = String(element);
      element++;
    }
  }

  return grid;
}


interface DraggableProps {
  handle?: boolean;
  identifier? : String
}



function DraggableItem({handle, identifier}: DraggableProps) {
  const {isDragging, setNodeRef, listeners} = useDraggable({
    id: String(identifier),
  });

   return (
    <Draggable
      dragging={isDragging}
      ref={setNodeRef}
      handle={handle}
      listeners={listeners}
      style={{
        opacity: isDragging ? 0 : undefined,
      }}

    />
  );
}


function DraggableItem2({handle, identifier}: DraggableProps) {
  const {isDragging, setNodeRef, listeners} = useDraggable({
    id: String(identifier),
  });
   return (
    <Draggable2
      dragging={isDragging}
      ref={setNodeRef}
      handle={handle}
      listeners={listeners}
      style={{
        opacity: isDragging ? 0 : undefined,
      }}

    />
