function buildGrid() {
  var grid: string[][] = [];
  var element = 1;
  for (let y = 1; y < 11; y++) {
    grid[y] = [];
    for (let x = 1; x < 11; x++) {
      grid[y][x] = String(element);
      element++;
    }
  }

  return grid;
}


interface DraggableProps {
  handle?: boolean;
  identifier? : String
}



function DraggableItem({handle, identifier}: DraggableProps) {
  const {isDragging, setNodeRef, listeners} = useDraggable({
    id: String(identifier),
  });

   return (
    <Draggable
      dragging={isDragging}
      ref={setNodeRef}
      handle={handle}
      listeners={listeners}
      style={{
        opacity: isDragging ? 0 : undefined,
      }}

    />
  );
}


function DraggableItem2({handle}: DraggableProps) {
  const {isDragging, setNodeRef, listeners} = useDraggable({
    id: 'draggable-item',
  });
   return (
    <Draggable2
   // identity={num_B}
      dragging={isDragging}
      ref={setNodeRef}
      handle={handle}
      listeners={listeners}
      style={{
        opacity: isDragging ? 0 : undefined,
      }}

    />
  );
}

export function GridGame({modifiers,}: Props) {
  var test : any;
    const grid = buildGrid();
    const [parents, setParent] = useState<UniqueIdentifier[]>(Array(25).fill(null));
    const [parent2, setParent2] = useState<UniqueIdentifier | null>(null);
    const [isDragging, setIsDragging] = useState<boolean[]>(Array(25).fill(false));
    const [isDragging2, setIsDragging2] = useState(false);
    const draggable2 = <DraggableItem2 />;

    var counter = -1;
    var draggableCheckers = new Map();

    return (
      <div>
      <DndContext 
        modifiers = {
          parents.every((element: String | null) => element === null) ? undefined: parent2 === null ? undefined: modifiers
        }
        onDragStart={(event) => {
          let newArray = [...isDragging];
          let index = event.active.id
          var getIndex = Number(index.match(/\d+$/));
          newArray[getIndex] = true;
          setIsDragging(newArray);
          setIsDragging2(true);
          /*
          if (event.active.id == "draggable-item"+num_G) {
            setIsDragging(true); 
          } else {
            setIsDragging2(true);
          } 
          */
        }}
        onDragEnd={({over}) => {
          for (var i = 0; i < isDragging.length; i++) {
            if (isDragging[i] == true) {
              //let newValue = draggableCheckers.get(i);
              let newArrayParent = [...parents];
              let newArrayDragging = [...isDragging];
              //let index = Number(isDragging.indexOf(isDragging[i]));

              newArrayParent[i] = (over ? over.id : null as any);
              setParent(newArrayParent);

              newArrayDragging[i] = false;
              setIsDragging(newArrayDragging);
              break;
            }

          }

         // setParent(prevParent => ({...prevState, email}));
          /*
          if (isDragging==true) {   
            setParent(over ? over.id : null);
            setIsDragging(false);
          } else {
            setParent2(over && isDragging2==true ? over.id : null);
            setIsDragging2(false);
          }
          */
        }}
        onDragCancel={(event) => {
          let newArray = [...isDragging];
          let index = event.active.id
          var getIndex = Number(index.match(/\d+$/));
          newArray[getIndex] = false;
          setIsDragging(newArray);

          setIsDragging2(false);
        }}
        > 
          <div className = "drop">
              {/*parents === null ? draggable : null*/}
          </div>
          <div className = "drop">  
              {parent2 === null ? draggable2 : null}
          </div>
          <div className = "grid">

            {grid.map((row: any[]) => { 
                return (
                  <div>
                    {row.map((tile:any) => {
                      var rowIndex = grid.indexOf(row);
                      if (tile == 1) {
                        draggableCheckers = new Map();
                        counter = -1;
                        //setParent(newParentArray);
                      }

                      if (((Number(tile)%2 == 0 && rowIndex%2 ==0) || (Number(tile)%2 != 0 && rowIndex%2 !=0)) && !draggableCheckers.has('draggable-item25')) {
                        //const test = draggable.type.parent;
                        counter++;
                        if (tile < 50) {
                          const draggable = <DraggableItem identifier = {'draggable-item'+(draggableCheckers.size)}/>;
                          draggableCheckers.set('draggable-item'+(draggableCheckers.size), draggable);

                          var index = 0;
                          draggableCheckers.forEach((value: JSX.Element, key : string) =>{
                            if (draggable === value) {
                              var getIndex = key.match(/\d+$/);
                              if (getIndex) {
                                index = Number(getIndex[0]);
                              }
                            }
                          });
                      }

                        //var result = parents.find(obj => obj = test);
                        draggableCheckers.get("");
                        var currentDrag = parents[counter];

                        draggableCheckers.get("draggable-item" + (draggableCheckers.size-1));
                        return (    
                          <div>
                            <Droppable_white key={tile} id={tile}>
                              {/*parents.forEach((parent) => {
                                {parent === null & tile < 50? draggable : null}
                              });*/}
                              {parents[draggableCheckers.size-1] === null && tile < 50? draggableCheckers.get("draggable-item" + (draggableCheckers.size-1)) : null}
                              {parents.indexOf(tile) > -1 ? draggableCheckers.get("draggable-item" + parents.indexOf(tile)): ''}
                            </Droppable_white> 
                          </div>  
                        ); 
                      } else {
                        return (      
                          <div>
                             <Droppable_Green id={tile}>

                            </Droppable_Green> 
                          </div> 
                        ); 
                      }
                     })}

                  </div>
                );
            })}

             {/*draggable*/}
            {/*<DragMain/>*/}
            </div>



              <DragOverlay>
                  {isDragging.length != 0 ? <Draggable dragging dragOverlay /> : null}
              </DragOverlay>


        </DndContext> 
        </div>

    );
}


export default GridGame;
