import React, { useState } from 'react';
import "./GridGame.css";
import {
  DndContext, 
  useDraggable, 
  DragEndEvent, 
  CollisionDetection as CollisionDetectionType, 
  Modifiers, 
  UniqueIdentifier,
  DragOverlay,
} from '@dnd-kit/core';
import {Draggable} from './Draggable';
import {Droppable} from './Droppable';

interface Props {
  modifiers?: Modifiers;
  grid?: string[][];
}

var currentTile = null;
/*
function Droppable(props) {
  const {isOver, setNodeRef} = useDroppable({
    id: props.id,
  });
  const style = {
    opacity: isOver ? 0.5 : 1,
  };

  return (
    <div className = "drop" ref={setNodeRef} style={style}>
      {props.children}
    </div>
  );
}
*/
/*
function DragMain() {
  const [parent, setParent] = useState(false);
  const [isDragging, setIsDragging] = useState(false);
  const draggable = (
    <Draggable id="draggable">
      drag
    </Draggable>
  );

  return (
    //<div className = "back">
      <DndContext onDragEnd={handleDragEnd}>
        {!parent ? draggable : false}
          <Droppable key = {"droppable"} id = {"droppable"} dragging = {isDragging}>
            {parent ? draggable : ''}
          </Droppable>
      </DndContext>
    //</div>
  );

  function handleDragEnd(event: DragEndEvent) {
    setParent(event.over ? !event.over.id : false);
    //if (event.over && event.over.id === 'droppable') {
      //setParent(true);
    //}
  }

}
*/

function buildGrid() {
  var grid: string[][] = [];
  var element = 0;
  for (let y = 0; y < 10; y++) {
    grid[y] = [];
    for (let x = 0; x < 10; x++) {
      grid[y][x] = String(element);
      element++;
    }
  }

  return grid;
}


interface DraggableProps {
  handle?: boolean;
}

function DraggableItem({handle}: DraggableProps) {
  const {isDragging, setNodeRef, listeners} = useDraggable({
    id: 'draggable-item',
  });

   return (
    <Draggable
      dragging={isDragging}
      ref={setNodeRef}
      handle={handle}
      listeners={listeners}
      style={{
        opacity: isDragging ? 0 : undefined,
      }}
    />
  );
}

export function GridGame({modifiers,}: Props) {
    var num = "-1";
    const grid = buildGrid();
    const [parent, setParent] = useState<UniqueIdentifier | null>(null);
    const [isDragging, setIsDragging] = useState(false);
    const [isDropped, setIsDropped] = useState(false);
    const draggable = <DraggableItem />;

   //const grid = Array(10).fill(Array(10).fill(<div className = "square_green"/>))

    //this.populateGrid();
    //this.createGrid(); 
   //const grid = Array(10).fill(Array(10).fill("A"))
    //const grid = Array(10).fill("A")
    return (

        <DndContext 
        modifiers={parent === null ? undefined : modifiers}
        onDragStart={() => setIsDragging(true)}
        onDragEnd={({over}) => {
          setParent(over ? over.id : null);
          setIsDragging(false);
          currentTile = parent;
        }}
        onDragCancel={() => setIsDragging(false)}
        > 
          <div className = "drop">
              {parent === null ? draggable : null}
          </div>
          <div className = "drop">
              {parent === null ? draggable : null}
          </div>
          <div className = "grid">
            {grid.map((row: any[]) => { 
                return (
                  <div>
                    {row.map((tile:any) => {
                      return (    
                        <div>  
                          <Droppable id={tile} dragging={isDragging}>
                            {parent === tile ? draggable : 'drop'}
                          </Droppable> 
                        </div> 
                      ); 
                     })}
                  </div>
                );
            })}

             {/*draggable*/}
            {/*<DragMain/>*/}

            </div>
            <DragOverlay>
                {isDragging ? <Draggable dragging dragOverlay /> : null}
            </DragOverlay>

        </DndContext> 

    );
}


export default GridGame;
