  if (!isRight && right >= finalScrollContainerRect.right - threshold.width) {
    // Scroll Right
    direction.x = Direction.Forward;
    speed.x =
      acceleration *
      Math.abs(
        ((finalScrollContainerRect.right - threshold.width) - right) /
          threshold.width
